@import "tailwindcss";
@import "tw-animate-css";
@import url('https://fonts.googleapis.com/css2?family=Plus+Jakarta+Sans:wght@300;400;500;600;700;800&display=swap');

@custom-variant dark (&:is(.dark *));

@theme inline {
  --radius-sm: calc(var(--radius) - 4px);
  --radius-md: calc(var(--radius) - 2px);
  --radius-lg: var(--radius);
  --radius-xl: calc(var(--radius) + 4px);
  --color-background: var(--background);
  --color-foreground: var(--foreground);
  --color-card: var(--card);
  --color-card-foreground: var(--card-foreground);
  --color-popover: var(--popover);
  --color-popover-foreground: var(--popover-foreground);
  --color-primary: var(--primary);
  --color-primary-foreground: var(--primary-foreground);
  --color-secondary: var(--secondary);
  --color-secondary-foreground: var(--secondary-foreground);
  --color-muted: var(--muted);
  --color-muted-foreground: var(--muted-foreground);
  --color-accent: var(--accent);
  --color-accent-foreground: var(--accent-foreground);
  --color-destructive: var(--destructive);
  --color-border: var(--border);
  --color-input: var(--input);
  --color-ring: var(--ring);
  --color-chart-1: var(--chart-1);
  --color-chart-2: var(--chart-2);
  --color-chart-3: var(--chart-3);
  --color-chart-4: var(--chart-4);
  --color-chart-5: var(--chart-5);
  --color-sidebar: var(--sidebar);
  --color-sidebar-foreground: var(--sidebar-foreground);
  --color-sidebar-primary: var(--sidebar-primary);
  --color-sidebar-primary-foreground: var(--sidebar-primary-foreground);
  --color-sidebar-accent: var(--sidebar-accent);
  --color-sidebar-accent-foreground: var(--sidebar-accent-foreground);
  --color-sidebar-border: var(--sidebar-border);
  --color-sidebar-ring: var(--sidebar-ring);
  --color-success: var(--success);
  --color-success-foreground: var(--success-foreground);
}

:root {
  --radius: 0.75rem;

  /* Brand Colors */
  --primary: hsl(222, 47%, 18%);
  --primary-light: hsl(222, 47%, 26%);
  --primary-foreground: hsl(0, 0%, 98%);

  --accent: hsl(38, 95%, 55%);
  --accent-foreground: hsl(222, 47%, 12%);
  --accent-light: hsl(38, 95%, 92%);

  /* Backgrounds */
  --background: hsl(220, 20%, 97%);
  --foreground: hsl(222, 47%, 12%);

  --card: hsl(0, 0%, 100%);
  --card-foreground: hsl(222, 47%, 12%);

  --popover: hsl(0, 0%, 100%);
  --popover-foreground: hsl(222, 47%, 12%);

  --secondary: hsl(220, 20%, 94%);
  --secondary-foreground: hsl(222, 47%, 18%);

  --muted: hsl(220, 14%, 91%);
  --muted-foreground: hsl(220, 9%, 46%);

  --border: hsl(220, 13%, 87%);
  --input: hsl(220, 13%, 87%);
  --ring: hsl(222, 47%, 18%);

  --destructive: hsl(0, 84%, 60%);
  --destructive-foreground: hsl(0, 0%, 98%);

  --success: hsl(142, 71%, 45%);
  --success-foreground: hsl(0, 0%, 98%);

  /* Charts */
  --chart-1: hsl(222, 47%, 35%);
  --chart-2: hsl(38, 95%, 55%);
  --chart-3: hsl(210, 60%, 55%);
  --chart-4: hsl(160, 60%, 45%);
  --chart-5: hsl(280, 50%, 55%);

  /* Sidebar */
  --sidebar: hsl(222, 47%, 14%);
  --sidebar-foreground: hsl(220, 20%, 85%);
  --sidebar-primary: hsl(38, 95%, 55%);
  --sidebar-primary-foreground: hsl(222, 47%, 12%);
  --sidebar-accent: hsl(222, 47%, 22%);
  --sidebar-accent-foreground: hsl(0, 0%, 98%);
  --sidebar-border: hsl(222, 47%, 22%);
  --sidebar-ring: hsl(38, 95%, 55%);

  /* Custom design tokens */
  --shadow-card: 0 1px 3px hsl(222 47% 18% / 0.08), 0 1px 2px hsl(222 47% 18% / 0.04);
  --shadow-card-hover: 0 4px 16px hsl(222 47% 18% / 0.12), 0 2px 4px hsl(222 47% 18% / 0.06);
  --shadow-elevated: 0 10px 30px -5px hsl(222 47% 18% / 0.15);
  --gradient-primary: linear-gradient(135deg, hsl(222, 47%, 18%), hsl(222, 47%, 28%));
  --gradient-accent: linear-gradient(135deg, hsl(38, 95%, 50%), hsl(38, 95%, 65%));
  --gradient-hero: linear-gradient(135deg, hsl(222, 47%, 18%) 0%, hsl(214, 55%, 25%) 100%);
  --transition-smooth: all 0.2s cubic-bezier(0.4, 0, 0.2, 1);
  --font-sans: 'Plus Jakarta Sans', sans-serif;
}

@layer base {
  * {
    @apply border-border outline-ring/50;
  }
  body {
    @apply bg-background text-foreground;
    font-family: var(--font-sans);
    -webkit-font-smoothing: antialiased;
  }
  h1, h2, h3, h4, h5, h6 {
    font-family: var(--font-sans);
    font-weight: 700;
  }
}

/* Sidebar styles */
.sidebar-nav-item {
  display: flex;
  align-items: center;
  gap: 0.75rem;
  padding: 0.625rem 1rem;
  border-radius: 0.5rem;
  color: var(--sidebar-foreground);
  font-size: 0.875rem;
  font-weight: 500;
  transition: var(--transition-smooth);
  cursor: pointer;
  text-decoration: none;
  border-left: 3px solid transparent;
  margin: 0 0.5rem;
}

.sidebar-nav-item:hover {
  background: var(--sidebar-accent);
  color: hsl(0, 0%, 98%);
}

.sidebar-nav-item.active {
  background: hsl(222, 47%, 22%);
  color: hsl(0, 0%, 98%);
  border-left-color: var(--accent);
}

.sidebar-nav-item.active svg {
  color: var(--accent);
}

/* KPI Card styles */
.kpi-card {
  background: var(--card);
  border: 1px solid var(--border);
  border-radius: var(--radius);
  padding: 1.5rem;
  box-shadow: var(--shadow-card);
  transition: var(--transition-smooth);
}

.kpi-card:hover {
  box-shadow: var(--shadow-card-hover);
  transform: translateY(-1px);
}

.kpi-hero {
  background: var(--gradient-hero);
  color: hsl(0, 0%, 98%);
  border: none;
  border-radius: var(--radius);
  padding: 2rem;
  box-shadow: var(--shadow-elevated);
}

/* Table styles */
.data-table {
  width: 100%;
  border-collapse: separate;
  border-spacing: 0;
}

.data-table th {
  text-align: left;
  padding: 0.75rem 1rem;
  font-size: 0.75rem;
  font-weight: 600;
  letter-spacing: 0.05em;
  text-transform: uppercase;
  color: var(--muted-foreground);
  border-bottom: 2px solid var(--border);
  background: hsl(220, 20%, 98%);
}

.data-table td {
  padding: 0.875rem 1rem;
  font-size: 0.875rem;
  border-bottom: 1px solid var(--border);
  color: var(--card-foreground);
}

.data-table tr:last-child td {
  border-bottom: none;
}

.data-table tbody tr {
  transition: background 0.15s ease;
}

.data-table tbody tr:hover td {
  background: hsl(220, 20%, 98%);
}

/* Badge styles */
.badge-paid {
  display: inline-flex;
  align-items: center;
  gap: 0.25rem;
  padding: 0.2rem 0.625rem;
  border-radius: 9999px;
  font-size: 0.75rem;
  font-weight: 600;
  background: hsl(142, 71%, 92%);
  color: hsl(142, 71%, 28%);
}

.badge-unpaid {
  display: inline-flex;
  align-items: center;
  gap: 0.25rem;
  padding: 0.2rem 0.625rem;
  border-radius: 9999px;
  font-size: 0.75rem;
  font-weight: 600;
  background: hsl(0, 84%, 94%);
  color: hsl(0, 84%, 40%);
}

/* Page header */
.page-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-bottom: 1.5rem;
}

.page-title {
  font-size: 1.5rem;
  font-weight: 800;
  color: var(--foreground);
  letter-spacing: -0.02em;
}

.page-subtitle {
  font-size: 0.875rem;
  color: var(--muted-foreground);
  margin-top: 0.125rem;
}

/* Scroll area */
.main-content {
  overflow-y: auto;
  height: 100vh;
  padding: 2rem;
  background: var(--background);
}

/* Form inputs */
.form-label {
  font-size: 0.875rem;
  font-weight: 600;
  color: var(--foreground);
  margin-bottom: 0.375rem;
  display: block;
}

/* Capacity bar */
.capacity-bar {
  width: 100%;
  height: 6px;
  background: var(--muted);
  border-radius: 3px;
  overflow: hidden;
}

.capacity-fill {
  height: 100%;
  border-radius: 3px;
  transition: width 0.3s ease;
}

/* Search input */
.search-bar {
  display: flex;
  align-items: center;
  gap: 0.5rem;
  background: var(--card);
  border: 1px solid var(--border);
  border-radius: 0.5rem;
  padding: 0.5rem 0.75rem;
  transition: var(--transition-smooth);
}

.search-bar:focus-within {
  border-color: var(--primary);
  box-shadow: 0 0 0 3px hsl(222 47% 18% / 0.08);
}

.search-bar input {
  border: none;
  outline: none;
  background: transparent;
  font-size: 0.875rem;
  color: var(--foreground);
  flex: 1;
}

.search-bar input::placeholder {
  color: var(--muted-foreground);
}

