            <Button onClick={handleSave} disabled={saving || !form.name || !form.email} style={{ background: 'var(--primary)', color: 'var(--primary-foreground)', fontWeight: 600 }}>
              {saving ? 'Wird gespeichert...' : editing ? 'Speichern' : 'Hinzufügen'}
