          </DialogFooter>
        </DialogContent>
      </Dialog>

      <ConfirmDeleteDialog
        open={!!deleteTarget}
        onClose={() => setDeleteTarget(null)}
        onConfirm={handleDelete}
        loading={deleting}
        title="Anmeldung löschen"
        description={`Möchten Sie diese Anmeldung wirklich löschen?`}
      />
    </div>
